client = openai.OpenAI(api_key=os.getenv("OPENAI_API_KEY1"))
TELEGRAM_TOKEN = os.getenv("TELEGRAM_TOKEN")

logging.basicConfig(level=logging.INFO)

# –°—Ç–∞—Ä—Ç
async def start(update: Update, context: ContextTypes.DEFAULT_TYPE):
    await update.message.reply_text("–ü—Ä–∏–≤–µ—Ç! –Ø GPT-–±–æ—Ç —Å –ø–∞–º—è—Ç—å—é. –ù–∞–ø–∏—à–∏ –º–Ω–µ —á—Ç–æ-–Ω–∏–±—É–¥—å.")

# –û—Å–Ω–æ–≤–Ω–æ–π –æ–±—Ä–∞–±–æ—Ç—á–∏–∫
async def chat_with_gpt(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_id = update.effective_user.id
    user_message = update.message.text

    # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    save_message(user_id, "user", user_message)

    # –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–µ 10 –ø–∞—Ä —Å–æ–æ–±—â–µ–Ω–∏–π
    messages = get_last_messages(user_id, limit=10)
    print(f'aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa  {messages}')
    # –î–æ–±–∞–≤–ª—è–µ–º system-–ø—Ä–æ–º–ø—Ç –≤ –Ω–∞—á–∞–ª–æ
    messages.insert(0, {
        "role": "system",
        "content": f"""–¢—ã –ø–æ–º–æ—â–Ω–∏–∫ –æ–ø–µ—Ä–∞—Ç–æ—Ä–æ–≤ –ê–ó–° –∫–æ–º–ø–∞–Ω–∏–∏ RedPetroleum
        –¢—ã –¥–æ–ª–∂–µ–Ω –æ—Ç–≤–µ—á–∞—Ç—å –Ω–∞ –≤–æ–ø—Ä–æ—Å—ã, –∫–∞—Å–∞—é—â–µ–µ—Å—è –±–∞–∑—ã –∑–Ω–∞–Ω–∏–π


        –ë–∞–∑–∞ –∑–Ω–∞–Ω–∏–π:

        {kb}"""
    })

    try:
        response = client.chat.completions.create(
        model="gpt-4o-mini",
        messages=messages
        )
        reply_text = response.choices[0].message.content

        # –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ç–≤–µ—Ç –±–æ—Ç–∞
        save_message(user_id, "assistant", reply_text)

        await update.message.reply_text(reply_text)

    except Exception as e:
        logging.error(f"–û—à–∏–±–∫–∞ GPT: {e}")
        await update.message.reply_text("–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–ø—Ä–æ—Å–µ –∫ GPT.")

# –°–±—Ä–æ—Å –ø–∞–º—è—Ç–∏
async def reset(update: Update, context: ContextTypes.DEFAULT_TYPE):
    user_id = update.effective_user.id
    from db import DB_FILE
    import sqlite3
    with sqlite3.connect(DB_FILE) as conn:
        conn.execute("DELETE FROM messages WHERE user_id = ?", (user_id,))
    await update.message.reply_text("–ü–∞–º—è—Ç—å –æ—á–∏—â–µ–Ω–∞!")

# –ó–∞–ø—É—Å–∫
def main():
    init_db()

    app = Application.builder().token(TELEGRAM_TOKEN).build()
    app.add_handler(CommandHandler("start", start))
    app.add_handler(CommandHandler("reset", reset))
    app.add_handler(MessageHandler(filters.TEXT & ~filters.COMMAND, chat_with_gpt))

    print("–ë–æ—Ç –∑–∞–ø—É—â–µ–Ω!")
    app.run_polling()

if __name__ == "__main__":
    main()